void
bcm_server_field_qualify_data(cpudb_key_t r_cpu, uint8 *r_pkt, void *r_cookie)
{
	uint8	*r_pp;
	uint32	r_seq;
	int	r_ret;
	int	r_i;
	int	unit;
	bcm_field_entry_t	eid;
	int	qual_id;
	uint16	length;
	uint8 *	r_p_data;
	uint8 *	r_p_mask;

	r_pp = r_pkt;
	BCM_UNPACK_U32(r_pp, r_seq);
	r_pp = r_pkt + BCM_RPC_HLEN;
	r_ret = BCM_E_NONE;
	BCM_UNPACK_U32(r_pp, unit);
	BCM_UNPACK_U16(r_pp, length);
	BCM_UNPACK_U32(r_pp, eid);
	BCM_UNPACK_U32(r_pp, qual_id);
	if (*r_pp++ == 1) {
		r_p_data = NULL;
	} else {
		r_p_data = sal_alloc(length*sizeof(uint8), "rpc data");
		if (r_p_data == NULL) {
			r_ret = BCM_E_MEMORY;
		} else {
			for (r_i = 0; r_i < length; r_i++) {
				BCM_UNPACK_U8(r_pp, *(r_p_data+r_i));
			}
		}
	}
	if (*r_pp++ == 1) {
		r_p_mask = NULL;
	} else {
		r_p_mask = sal_alloc(length*sizeof(uint8), "rpc mask");
		if (r_p_mask == NULL) {
			r_ret = BCM_E_MEMORY;
		} else {
			for (r_i = 0; r_i < length; r_i++) {
				BCM_UNPACK_U8(r_pp, *(r_p_mask+r_i));
			}
		}
	}
	bcm_rpc_free(r_pkt, r_cookie);

	if (r_ret == BCM_E_NONE) {
		r_ret = bcm_field_qualify_data(unit, eid, qual_id, r_p_data, r_p_mask, length);
	}

	if (r_p_data != NULL) {
		sal_free(r_p_data);
	}
	if (r_p_mask != NULL) {
		sal_free(r_p_mask);
	}

	r_pkt = bcm_rpc_setup('S', (uint32 *)0, 4, r_seq, r_ret);
	r_pp = r_pkt + BCM_RPC_HLEN+4;

	bcm_rpc_reply(r_cpu, r_pkt, r_pp-r_pkt);
}

